<?phpfunction getOrderCopyMailBody($ordersid){	global $dbsite;
	global $owner;	global $www_url;
	$sqltext = "SELECT * FROM orders,supp	WHERE orders.suppid=supp.suppid	AND orders.ordersid=". $ordersid ;	$res = $dbsite->query($sqltext); # or die("Cant get order");	if (! empty($res)) {		$r = $res[0];	}else{		return 0;	}	$date = date("d/m/Y", $r->incept);	$dateoff = date("d/m/Y", $r->dateoff);	$foa = '';	$content = stripslashes($r->content);	$retHTML = <<<EOHTML	<div style="text-align:right;">	<img src="$www_url/img/co.logo.png" border="0" width="247" align="left"><br><br>	Quote To:-<br>	{$owner->co_name}<br>	{$owner->add1},<br>	{$owner->add2} {$owner->add3}<br>	{$owner->city},	{$owner->county}<br>	{$owner->country}	{$owner->postcode}<br>	Tel: {$owner->tel}<br>	Fax: {$owner->fax}<br>	Email: {$owner->email}<br>	Website: {$owner->web}	</div>		<br clear="all">	<h1>		Copy of Quote to {$owner->co_name}	</h1>	<div style="float:right;text-align:right;padding:10px;">		<table cellpadding=4>		<tr>			<td align=right>Date:</td><td>$date</td>		</tr>		<tr>			<td align=right>{$owner->co_nick} Order No:</td><td>$ordersid</td>		</tr>		<tr>			<td align=right>{$owner->co_nick} Job No:</td><td>{$r->jobno}</td>		</tr>		<tr>			<td align=right>VAT Reg No:</td><td>{$owner->vat_no}</td>		</tr>		</table>	</div>Quote from:-<br>{$r->co_name}<br>{$r->add1}      {$r->add2}      {$r->add3}<br>{$r->city}      {$r->county}    {$r->postcode}$foa	<br clear="all"><span style="font-size:90%;font-weight:bold;">Below is a copy of the quote you sent to {$owner->co_name}</span><br><table  border="0" cellpadding="4" cellspacing="4" width="440">  <tr style="font-size:80%;font-weight:bold;">  <td valign="top">Description</td>  <td valign="top">Quantity</td>  <td valign="top">Price</td>  <td valign="top">Promised Delivery Date</td>  </tr>  <tr style="font-size:120%;">  <td valign="top">$content</td>  <td valign="top">X {$r->quantity}</td>  <td valign="top">&pound;{$r->priceoff}</td>  <td valign="top">$dateoff</td>  </tr></table><br><br><br clear="all"><br clear="both" />EOHTML;return ($retHTML);}function getPurchaseOrderMailBody($ordersid){	global $www_url;	$sqltext = "SELECT * FROM orders,supp	WHERE orders.suppid=supp.suppid	AND orders.ordersid=". $ordersid ;	$res = $dbsite->query($sqltext); # or die("Cant get order");	if (! empty($res)) {		$r = $res[0];	}else{		return 0;	}	$date = date("d/m/Y", $r->incept);	$dateok = date("d/m/Y", $r->dateok);	$priceok = '&pound; ' . $r->priceok;	$foa = '';	$content = stripslashes($r->content);	$retHTML = <<<EOHTML	<div style="text-align:right;"><img src="$www_url/img/co.logo.png" border="0" width="247" align="left"><br><br>	{$owner->co_name}<br>	{$owner->add1},<br>	{$owner->add2} {$owner->add3}<br>	{$owner->city},	{$owner->county}<br>	{$owner->country}	{$owner->postcode}<br>	Tel: {$owner->tel}<br>	Fax: {$owner->fax}<br>	Email: {$owner->email}<br>	Website: {$owner->web}</div>	<br clear="all"><h1>Purchase Order</h1><div style="float:right;text-align:right;padding:10px;"><table cellpadding=4><tr><td align=right>Order No:</td><td>$ordersid</td></tr><tr><td align=right>Date:</td><td>$date</td></tr><tr><td align=right>{$owner->co_nick} Job No:</td><td>{$r->jobno}</td></tr><tr><td align=right>VAT Reg No:</td><td>114 6698 58</td></tr></table></div>To:-<br>{$r->co_name}<br>{$r->add1}      {$r->add2}      {$r->add3}<br>{$r->city}      {$r->county}    {$r->postcode}$foa<br clear="all"><span style="font-size:90%;font-weight:bold;">Please supply the following items as agreed:-</span><br><table  border="0" cellpadding="4" cellspacing="4" width="440"><tr style="font-size:80%;font-weight:bold;"><td valign="top">Description</td><td valign="top">Quantity</td></tr><tr style="font-size:120%;"><td valign="top">$content</td><td valign="top">X {$r->quantity}</td></tr></table><p>Date Agreed: $dateok</p><p>Price Agreed: $priceok</p><br clear="all"><br clear="both" />Best Regards,<br><br>{$owner->co_name}<br><br><br><br>EOHTML;return ($retHTML);}?>